import React, { useState, useEffect } from 'react';
import Map from './components/Map';
import FilterPanel from './components/FilterPanel';
import LocationList from './components/LocationList';
import './App.css';

function App() {
  const [locations, setLocations] = useState([]);
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [selectedTypes, setSelectedTypes] = useState({
    foodbank: true,
    community_fridge: true,
    food_box: true
  });
  const [selectedLocation, setSelectedLocation] = useState(null);

  useEffect(() => {
    // Fetch locations from API
    fetch('http://localhost:5000/api/locations')
      .then(res => res.json())
      .then(data => {
        setLocations(data);
        setFilteredLocations(data);
      })
      .catch(err => {
        console.error('Error fetching locations:', err);
        // Fallback to sample data if API is not available
        const sampleData = require('./data/sampleLocations.json');
        setLocations(sampleData);
        setFilteredLocations(sampleData);
      });
  }, []);

  useEffect(() => {
    // Filter locations based on selected types
    const filtered = locations.filter(location => selectedTypes[location.type]);
    setFilteredLocations(filtered);
  }, [selectedTypes, locations]);

  const handleFilterChange = (type) => {
    setSelectedTypes(prev => ({
      ...prev,
      [type]: !prev[type]
    }));
  };

  const handleLocationClick = (location) => {
    setSelectedLocation(location);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>üçé FoodFinder Denver</h1>
        <p>Find free food resources in Denver, Colorado</p>
      </header>
      
      <div className="app-container">
        <aside className="sidebar">
          <FilterPanel 
            selectedTypes={selectedTypes}
            onFilterChange={handleFilterChange}
          />
          <LocationList 
            locations={filteredLocations}
            onLocationClick={handleLocationClick}
            selectedLocation={selectedLocation}
          />
        </aside>
        
        <main className="map-container">
          <Map 
            locations={filteredLocations}
            selectedLocation={selectedLocation}
            onLocationClick={handleLocationClick}
          />
        </main>
      </div>
    </div>
  );
}

export default App;
