import React, { useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';

// Fix for default marker icons in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Custom icons for different location types
const createCustomIcon = (color) => {
  return L.divIcon({
    className: 'custom-marker',
    html: `<div style="background-color: ${color};" class="marker-pin"></div>`,
    iconSize: [30, 42],
    iconAnchor: [15, 42],
    popupAnchor: [0, -42]
  });
};

const icons = {
  foodbank: createCustomIcon('#43a047'),
  community_fridge: createCustomIcon('#66bb6a'),
  food_box: createCustomIcon('#fdd835')
};

// Component to handle map centering when location is selected
function MapController({ selectedLocation, cityCenter }) {
  const map = useMap();
  const prevCityCenter = React.useRef(cityCenter);
  
  useEffect(() => {
    if (selectedLocation) {
      map.flyTo(
        [selectedLocation.coordinates.lat, selectedLocation.coordinates.lng],
        15,
        { duration: 1 }
      );
    }
  }, [selectedLocation, map]);

  useEffect(() => {
    // Only fly to city if the city center actually changed (not just a re-render)
    const prev = prevCityCenter.current;
    if (cityCenter && prev && 
        (prev[0] !== cityCenter[0] || prev[1] !== cityCenter[1])) {
      map.flyTo(cityCenter, 12, { duration: 1.5 });
    }
    prevCityCenter.current = cityCenter;
  }, [cityCenter, map]);
  
  return null;
}

// Component to handle map clicks
function MapClickHandler({ onMapClick }) {
  useMapEvents({
    click: (e) => {
      if (onMapClick) {
        onMapClick({
          lat: e.latlng.lat,
          lng: e.latlng.lng
        });
      }
    }
  });
  return null;
}

function Map({ locations, selectedLocation, onLocationClick, onMapClick, cityCenter, isSelectingOnMap }) {
  const defaultCenter = cityCenter || [39.7392, -104.9903]; // Fallback to Denver
  const defaultZoom = 12;

  const handleMapClick = (e) => {
    if (onMapClick) {
      onMapClick({
        lat: e.latlng.lat,
        lng: e.latlng.lng
      });
    }
  };

  return (
    <>
      {isSelectingOnMap && (
        <div className="selecting-overlay">
          <div className="selecting-message">
            üìç Click anywhere on the map to select a location
          </div>
        </div>
      )}
      <MapContainer
        center={defaultCenter}
        zoom={defaultZoom}
        className={`leaflet-map ${isSelectingOnMap ? 'selecting-mode' : ''}`}
        onClick={handleMapClick}
      >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      
      <MapController selectedLocation={selectedLocation} cityCenter={cityCenter} />
      
      {locations.map(location => (
        <Marker
          key={location.id}
          position={[location.coordinates.lat, location.coordinates.lng]}
          icon={icons[location.type]}
          eventHandlers={{
            click: () => onLocationClick(location)
          }}
        >
          <Popup>
            <div className="popup-content">
              <h3>{location.name}</h3>
              <p className="popup-type">{location.type.replace('_', ' ').toUpperCase()}</p>
              <p><strong>Address:</strong> {location.address}</p>
              <p><strong>Hours:</strong> {location.hours}</p>
              {location.phone && <p><strong>Phone:</strong> {location.phone}</p>}
              <p className="popup-description">{location.description}</p>
            </div>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
    </>
  );
}

export default Map;
