import React, { useState, useEffect } from 'react';
import Map from './components/Map';
import FilterPanel from './components/FilterPanel';
import LocationList from './components/LocationList';
import SubmissionForm from './components/SubmissionForm';
import './App.css';

function App() {
  const [locations, setLocations] = useState([]);
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [selectedCity, setSelectedCity] = useState('denver');
  const [selectedTypes, setSelectedTypes] = useState({
    foodbank: true,
    community_fridge: true,
    food_box: true
  });
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [isSubmissionFormOpen, setIsSubmissionFormOpen] = useState(false);

  const cityConfig = {
    denver: {
      name: 'Denver',
      state: 'Colorado',
      center: [39.7392, -104.9903]
    },
    seattle: {
      name: 'Seattle',
      state: 'Washington',
      center: [47.6062, -122.3321]
    }
  };

  useEffect(() => {
    // Fetch locations from API
    fetch(`http://localhost:5000/api/locations?city=${selectedCity}`)
      .then(res => res.json())
      .then(data => {
        setLocations(data);
        setFilteredLocations(data);
      })
      .catch(err => {
        console.error('Error fetching locations:', err);
        // Fallback to sample data if API is not available
        const sampleData = require('./data/sampleLocations.json');
        setLocations(sampleData);
        setFilteredLocations(sampleData);
      });
  }, [selectedCity]);

  useEffect(() => {
    // Filter locations based on selected types
    const filtered = locations.filter(location => selectedTypes[location.type]);
    setFilteredLocations(filtered);
  }, [selectedTypes, locations]);

  const handleFilterChange = (type) => {
    setSelectedTypes(prev => ({
      ...prev,
      [type]: !prev[type]
    }));
  };

  const handleLocationClick = (location) => {
    setSelectedLocation(location);
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-content">
          <div className="header-title">
            <h1>üçé FreeFoodFinder</h1>
            <div className="city-selector">
              <select 
                value={selectedCity} 
                onChange={(e) => setSelectedCity(e.target.value)}
                className="city-dropdown"
              >
                <option value="denver">Denver, CO</option>
                <option value="seattle">Seattle, WA</option>
              </select>
            </div>
          </div>
          <button 
            className="suggest-button"
            onClick={() => setIsSubmissionFormOpen(true)}
          >
            + Suggest Location
          </button>
        </div>
      </header>
      
      <div className="app-container">
        <aside className="sidebar">
          <FilterPanel 
            selectedTypes={selectedTypes}
            onFilterChange={handleFilterChange}
          />
          <LocationList 
            locations={filteredLocations}
            onLocationClick={handleLocationClick}
            selectedLocation={selectedLocation}
          />
        </aside>
        
        <main className="map-container">
          <Map 
            locations={filteredLocations}
            selectedLocation={selectedLocation}
            onLocationClick={handleLocationClick}
            cityCenter={cityConfig[selectedCity].center}
          />
        </main>
      </div>
      
      <SubmissionForm 
        isOpen={isSubmissionFormOpen}
        onClose={() => setIsSubmissionFormOpen(false)}
      />
    </div>
  );
}

export default App;
